INSERT INTO `recipes` (`id`, `title`, `prep_time`, `cooking_time`, `serving_qty`, `ingredients`, `steps`, `tips`, `source`, `image`, `created_at`, `updated_at`) VALUES
(1, 'ভুনা মাংস', '১৫ মিনিট', '৫০ মিনিট', '৪ জনের পরিবেশন', '১. গরুর মাংস (হাড় ছাড়া) আধা কেজি,\r\n২. সরিষার তেল ২ টেবিল চামচ,\r\n৩. পেঁয়াজ (বড়) ৬টি,\r\n৪. শাহি জিরা ১ টেবিল চামচ,\r\n৫. আদা বাটা ২ টেবিল চামচ,\r\n৬. রসুন বাটা ২ টেবিল চামচ,\r\n৭. সরিষার দানা ১ টেবিল চামচ,\r\n৮. লবণ স্বাদমতো,\r\n৯. হলুদ গুঁড়া ১ চা চামচ,\r\n১০. মরিচ গুঁড়া ২ চা চামচ,\r\n১১. গরম মসলা গুঁড়া ২ টেবিল চামচ,\r\n১২. কাঁচা মরিচ ৮টি (মাঝখান থেকে চেরা),\r\n১৩. ধনিয়াপাতা কুচি সাজানোর জন্য।', '> পাত্রে সরিষার তেল গরম করে তাতে পেঁয়াজ বাদামি করে ভাজুন। \r\n> এরপর এতে মাংস দিয়ে ১০ মিনিটের মতো হালকা আঁচে রাখুন। শাহি জিরা, সরিষার দানা দিয়ে আরো মিনিট পাঁচেক হালকা আঁচে চুলায় রাখুন। \r\n> কাঁচা মরিচ, আদা-রসুন বাটা, লবণ, হলুদ গুঁড়া, মরিচ গুঁড়া, গরম মসলা গুঁড়া পাত্রে মাংসের সঙ্গে ভালোমতো মিশিয়ে ঢেকে আরো ১৫ মিনিটের মতো বা মাংস সিদ্ধ হওয়া পর্যন্ত চুলায় রাখুন। \r\n> এরপর পরিবেশন পাত্রে ঢেলে ধনিয়াপাতা কুচি দিয়ে সাজিয়ে পরিবেশন করুন।', '> গরুর মাংস কেনার সময় নরম মাংস বেছে নিতে হবে। এ জন্য গরুর পিঠের দিকের বা পেছনের অংশের মাংস বেশ ভালো। আর কারিপাতা দিলে স্বাদ আরো ভালো হয়। চুলা থেকে নামানোর ৫ মিনিট আগে থাই লেবুর পাতা দিলে একটু টক স্বাদ আসবে।', 'সালাম, কালের কন্ঠ', 'ভুনা মাংস_1536084067.jpg', '2018-09-04 22:01:07', '2018-09-04 22:01:07'),
(2, 'কালা ভুনা', NULL, NULL, NULL, '১. গরুর মাংস সোয়া ১ কেজি,\r\n২. পেঁয়াজ ২ কাপ,\r\n৩. আদা-রসুন বাটা ২ টেবিল-চামচ,\r\n৪. কালো গোলমরিচ ২ চা-চামচ,\r\n৫. কাবাব চিনি ৪-৫টা,\r\n৬. রাঁধুনি ও শাহি জিরা ২ চা-চামচ (টেলে গুঁড়া করে নেয়া),\r\n৭. গরমমসলা গুঁড়া ২ চা-চামচ,\r\n৮. ধনিয়া গুঁড়া ২ চা-চামচ,\r\n৯. লাল মরিচ গুঁড়া ২ চা-চামচ,\r\n১০. হলুদ গুঁড়া ১ চা-চামচ,\r\n১১. জিরা গুঁড়া ১ চা-চামচ,\r\n১২. শুকনা মরিচ ৭-৮টা,\r\n১৩. কাঁচা মরিচ ১০-১২টা,\r\n১৪. আস্ত রসুনের কোয়া ৫-৬টা,\r\n১৫. সয়াবিন তেল আধা কাপ,\r\n১৬. সরিষার তেল ১ কাপ,\r\n১৭. লবণ স্বাদমতো।', '> গরুর মাংস ছোট ছোট টুকরা করে কেটে ধুয়ে যে হাঁড়িতে রান্না করবেন তাতে রেখে দিন। এর সঙ্গে সয়াবিন তেল, আস্ত রসুন, শুকনা মরিচ ও পেঁয়াজ ছাড়া বাকি সব উপাদান দিয়ে ভালো করে মাখিয়ে ২ কাপ পানি দিন। এবার চুলায় দিয়ে ঢেকে মাংস সেদ্ধ না হওয়া পর্যন্ত রান্না করে নিন। মাঝে মাঝে নেড়েচেড়ে কষাতে থাকুন। মাংস সেদ্ধ হয়ে কালো হলে চুলা থেকে নামিয়ে নিন। এবার অন্য একটি কড়াইয়ে সরিষার তেল গরম করে তাতে পেঁয়াজ বেরেস্তা করে নিন। শুকনা মরিচ আর রসুনের কোয়া লাল করে ভেজে বেরেস্তাসহ রান্না করা মাংসের মধ্যে ঢেলে দিন। তারপর চুলায় বসিয়ে নাড়তে নাড়তে কমপক্ষে আধঘণ্টা ভাজতে হবে। মাংস কালো হয়ে আর খানিকটা ভাজা হয়ে এলে নামানোর আগে অল্প একটু রাঁধুনি গুঁড়া আর গরমমসলা গুঁড়া ছিটিয়ে দিয়ে নামিয়ে নিন। এবার সুন্দর করে সাজিয়ে পরিবেশন করুন।', NULL, 'ফাতেমা আবেদীন, ক্যানভাস', 'কালা ভুনা_1536084358.jpg', '2018-09-04 22:05:58', '2018-09-04 22:05:58'),
(3, 'মেজবান মাংস', NULL, NULL, NULL, 'উপকরণ (১) :\r\n১. গরুর মাংস চার কেজি,\r\n২. পেঁয়াজ (অর্ধেক বাটা, অর্ধেক কুচি) ২ কেজি,\r\n৩. আদা বাটা ২০০ গ্রাম,\r\n৪. রসুন বাটা ২০০ গ্রাম,\r\n৫. সাদা সরিষা বাটা ৫০ গ্রাম,\r\n৬. চিনাবাদাম বাটা ৫০ গ্রাম,\r\n৭. নারকেল বাটা ২০০ গ্রাম,\r\n৮. ধনিয়া গুঁড়া ২ টেবিল-চামচ,\r\n৯. জিরা গুঁড়া ২ টেবিল-চামচ,\r\n১০. মরিচ গুঁড়া ৩ টেবিল-চামচ,\r\n১১. হলুদ গুঁড়া ২ টেবিল-চামচ,\r\n১২. গরমমসলা পরিমাণমতো,\r\n১৩. টমেটো ১ কেজি,\r\n১৪. সরিষার তেল আধা কেজি,\r\n১৫. ঘি ৩৫০ গ্রাম,\r\n১৬. কাঁচা মরিচ ১০টি,\r\n১৭. লবণ স্বাদমতো।\r\n.\r\n.\r\nউপকরণ (২):\r\n১. জিরা ২০ গ্রাম,\r\n২. ধনিয়া ১০ গ্রাম,\r\n৩. রাঁধুনি ১৫ গ্রাম,\r\n৪. শুকনা মরিচ ১০টি,\r\n৫. তেজপাতা ৮টি।\r\n.\r\n.\r\nউপকরণ (৩):\r\n১. মুখ চেরা এলাচি ৬টি,\r\n২. দারুচিনি (২ ইঞ্চি) ৩টি,\r\n৩. লবঙ্গ ৮টি,\r\n৪. গোলমরিচ আধা টেবিল-চামচ,\r\n৫. মেথি ২ টেবিল-চামচ,\r\n৬. জায়ফল ১টি,\r\n৭. জয়ত্রী ১ টেবিল-চামচ,\r\n৮. রাঁধুনি আধা টেবিল-চামচ,\r\n৯. জোয়াইন ১ চা-চামচ।', '> মাংস টুকরা ধুয়ে পানি ঝরাতে হবে। গরম পানি ও কাঁচা মরিচ ছাড়া ১ নম্বর উপকরণের সব মসলা ও ২৫০ গ্রাম ঘি দিয়ে মাংস মেখে একটি ভারী সসপ্যানে নিয়ে চুলায় বসাতে হবে। ২ কাপ পানি দিয়ে নাড়–ন। এবার অন্য একটি কড়াইয়ে ২ নম্বর উপকরণের মসলাগুলো ভেজে গুঁড়া করে মাংসে দিন। ঢাকনা দিয়ে চুলায় মাঝারি আঁচে রান্না করতে হবে। মাঝেমধ্যে নেড়েচেড়ে দিন। পানি শুকিয়ে এলে সামান্য গরম পানি দিতে হবে, তবে বেশি নয়। মাখা মাখা ঝোল রাখতে হবে। এর মধ্যে ৩ নম্বর উপকরণের মসলা ভেজে গুঁড়া করে রাখতে হবে। মাংস সেদ্ধ হয়ে উপরে তেল ভেসে উঠলে কাঁচা মরিচ এবং ৩ নম্বর উপকরণের গুঁড়া মসলা ও ১০০ গ্রাম ঘি দিয়ে নেড়েচেড়ে নামিয়ে নিতে হবে।', NULL, 'ফাতেমা আবেদীন, ক্যানভাস', 'মেজবান মাংস_1536084620.jpg', '2018-09-04 22:10:20', '2018-09-04 22:10:20'),
(4, 'কালোজিরা গোশ', NULL, NULL, NULL, '১. এক কেজি গরুর গোশত (হাড়সহ),\r\n২. এক টেবিল-চামচ রসুন বাটা,\r\n৩. এক টেবিল-চামচ আদা বাটা,\r\n৪. গরমমসলা (এলাচি, দারুচিনি),\r\n৫. দুই চা-চামচ কাঁচা মরিচ পেস্ট,\r\n৬. এক চা-চামচ হলুদ,\r\n৭. এক কাপ পেঁয়াজ কুচি বা বাটা,\r\n৮. এক চিমটি জিরা গুঁড়া,\r\n৯. এক মুঠো কাঁচা মরিচ,\r\n১০. লবণ স্বাদমতো,\r\n১১. পরিমাণমতো,\r\n১২. পরিমানমতোপানি,\r\n১৩. কালোজিরা আধা চা-চামচ।', '> আধা কাপ পেঁয়াজ, কালোজিরা ছাড়া সব মসলা দিয়ে মাংস মাখিয়ে চুলায় দিন। কিছুক্ষণ নেড়েচেড়ে মাংস কষানো হয়ে গেলে গরম পানি দিতে হবে। ঝোল ফুটে উঠে মাংস সেদ্ধ হয়ে এলে আরেক চুলায় পেঁয়াজ বেরেস্তা করে তাতে কালোজিরা ছেড়ে দিন। এবার এই ফোড়ন মাংসের উপর ঢেলে দিয়ে নামিয়ে নিন। গরম ভাত, রুটি যা খুশি দিয়ে পরিবেশন করুন।', NULL, 'ফাতেমা আবেদীন, ক্যানভাস', 'কালোজিরা গোশ_1536084751.jpg', '2018-09-04 22:12:32', '2018-09-04 22:12:32'),
(5, 'চুইঝালে গরুর মাংস', NULL, NULL, NULL, '১. গরুর মাংস ২ কেজি,\r\n২. রসুন কুচি ১ কাপ,\r\n৩. পেঁয়াজ আধা কাপ,\r\n৪. জিরা ২ টেবিল-চামচ,\r\n৫. শুকনা মরিচ গুঁড়া ২ টেবিল-চামচ,\r\n৬. আদা বাটা ১ টেবিল-চামচ,\r\n৭. এলাচি ৪টি, দারুচিনি ২টি,\r\n৮. তেজপাতা ৩-৪টি,\r\n৯. তেল ১ কাপ,\r\n১০. লবণ পরিমাণমতো,\r\n১১. লবঙ্গ ৪-৫টি,\r\n১২. ভাজা মসলা (ধনিয়া, জিরা, এলাচি ও দারুচিনি) ১ টেবিল-চামচ,\r\n১৩. চুইঝাল ২৫০ গ্রাম (বা ইচ্ছামতো),\r\n১৪. হলুদ ২ চা-চামচ।', '> গরুর মাংসের চর্বি ফেলে ভালোভাবে ধুয়ে নিতে হবে। তারপর চুইঝাল ও ভাজা মসলা ছাড়া বাকি সব মসলা দিয়ে মাখিয়ে চুলায় চড়িয়ে দিন। কিছুক্ষণ মাংস ভালোভাবে কষানোর পর তেল উপরে উঠে এলে তাতে আধা লিটার গরম পানি দিয়ে আবার ২০ মিনিট কষাতে হবে। মাংস আধা সেদ্ধ হলে চুইঝাল দিয়ে ঢেকে দিতে হবে। সেদ্ধ হলে ভাজা মসলা দিয়ে নামিয়ে রুটি, পরোটা ও গরম ভাত দিয়ে পরিবেশন করুন। চুইঝালের মাংস বড় বড় করে কাটতে হয়। মাংসের সঙ্গে যেন হাড় থাকে। তবে চর্বি ফেলে দিতে হবে পুরোপুরি।', NULL, 'ফাতেমা আবেদীন, ক্যানভাস', 'চুইঝালে গরুর মাংস_1536156934.jpg', '2018-09-05 18:15:34', '2018-09-05 18:15:34'),
(6, 'মাংসের পিঠালি', NULL, NULL, NULL, '১. এক কেজি গরুর গোশত (হাড়সহ),\r\n২. ছোট আলু ১০-১২টা ছিলে নেয়া,\r\n৩. এক টেবিল-চামচ রসুন বাটা (বেশি দিলেও সমস্যা নেই),\r\n৪. এক টেবিল-চামচ আদা বাটা,\r\n৫. কিছু গরমমসলা (এলাচি, দারুচিনি),\r\n৬. চার চা-চামচ মরিচ গুঁড়া (ঝাল অনেক বেশি দিতে হয়),\r\n৭. এক চা-চামচ হলুদ,\r\n৮. এক কাপ পেঁয়াজ কুচি বা বাটা,\r\n৯. এক চিমটি জিরা গুঁড়া,\r\n১০. এক মুঠো কাঁচা মরিচ,\r\n১১. লবণ (স্বাদমতো),\r\n১২. পরিমাণমতো তেল ও পানি,\r\n১৩. কালোজিরা আধা চা-চামচ,\r\n১৪. চালের গুঁড়া বা শিল-পাটায় বেটে নেয়া চাল ২ টেবিল-চামচ।', '> আধা কাপ পেঁয়াজ, কালোজিরা, আলু ও চালের গুঁড়া ছাড়া সব মসলা দিয়ে মাংস মাখিয়ে চুলায় দিন। মাংস কষানো হয়ে গেলে গরম পানি ও আলু দিতে হবে। ঝোল ফুটে উঠলে তাতে চালের গুঁড়া পানিতে গুলিয়ে ছেড়ে দিন। এরপর মাংস ফুটে ওঠা পর্যন্ত অপেক্ষা করুন। অন্য একটি কড়াইয়ে পেঁয়াজ বেরেস্তা করে তাতে কালোজিরার ফোড়ন দিন। তারপর এই ফোড়ন মাংসের উপর ঢেলে দিয়ে নামিয়ে নিন। ভীষণ ঝাল এই মাংস গরম ভাত দিয়ে পরিবেশন করুন।', NULL, 'ফাতেমা আবেদীন, ক্যানভাস', 'মাংসের পিঠালি_1536157386.jpg', '2018-09-05 18:23:07', '2018-09-05 18:23:07'),
(7, 'রসুনে গরুর ঝুরি ভাজা', NULL, NULL, NULL, '১. এক কেজি গরুর গোশত (হাড়সহ),\r\n২. এক টেবিল-চামচ রসুন বাটা,\r\n৩. এক টেবিল-চামচ আদা বাটা,\r\n৪. কিছু গরমমসলা (এলাচি, দারুচিনি),\r\n৫. এক চা-চামচ মরিচ গুঁড়া,\r\n৬. এক চা-চামচ হলুদ,\r\n৭. এক কাপ পেঁয়াজ কুচি বা বাটা,\r\n৮. এক চিমটি জিরা গুঁড়া,\r\n৯. লবণ স্বাদমতো,\r\n১০. পরিমাণমতো তেল ও \r\n১১. পরিমানমতো পানি,\r\n১২. আস্ত রসুনের কোয়া ১ কাপ,\r\n১৩. বড় করে কাটা পেঁয়াজের ফালি ১ কাপ।', '> মাংসে রসুন ও পেঁয়াজ ফালি ছাড়া সব উপকরণ দিয়ে ভালো করে কষিয়ে পানি শুকিয়ে ফেলতে হবে। এরপর হাত দিয়ে বা হামাম দিস্তায় কষানো মাংস ঝুরি করে নিতে হবে। অন্য একটি চুলায় আধা কাপ তেল দিয়ে তাতে রসুন ও পেঁয়াজ ফালি দিয়ে ভাজতে হবে। হালকা ভাজা ভাজা অবস্থায় ঝুরি করা মাংস ছেড়ে দিয়ে সেটি অল্প আঁচে দীর্ঘক্ষণ ভাজতে হবে। রসুনের ঘ্রাণ ছড়িয়ে মাংস মুচমুচে ভাজা হয়ে এলে নামিয়ে নিতে হবে। পরিবেশন করতে হবে গরম গরম।', NULL, 'ফাতেমা আবেদীন, ক্যানভাস', 'রসুনে গরুর ঝুরি ভাজা_1536157526.jpg', '2018-09-05 18:25:26', '2018-09-05 18:25:26'),
(8, 'গরুর চাপ কাবাব', NULL, NULL, NULL, '১. গরুর মাংস (৪০০ গ্রাম ওজনের একটি টিবোন স্টেক নেয়া যেতে পারে),\r\n২. টক দই ২ টেবিল-চামচ,\r\n৩. সয়াবিন তেল আধা কাপ,\r\n৪. জিরা বাটা ১ চা-চামচ,\r\n৫. মরিচ গুঁড়া ১ চা-চামচ,\r\n৬. আদা বাটা ১ টেবিল-চামচ,\r\n৭. রসুন বাটা ১ টেবিল-চামচ,\r\n৮. কাবাব মসলা ১ টেবিল-চামচ,\r\n৯. লবণ স্বাদমতো।', '> একটি মিটহ্যামার (মাংস ছেঁচার হাতুড়ি) দিয়ে ভালো করে মাংস ছেঁচে নিন। মাংসের আকৃতি একটু বড় হলে ভালো হয়। এবার সব উপকরণ দিয়ে মাংস খুব ভালো করে মেখে ৩ থেকে ৪ ঘণ্টা মেরিনেট করে রাখুন। তারপর একটি পুরু লোহার তাওয়ায় মাখানো মাংস দিয়ে মাঝারি আঁচে চুলায় গরম হতে দিন। তেলে মাখানো মাংস হালকা আঁচে দীর্ঘক্ষণ ভাজতে থাকুন। মাংস ভাজা ভাজা হয়ে সেদ্ধ হয়ে এলে চুলা থেকে নামিয়ে নিন। লুচি দিয়ে পরিবেশন করুন।', NULL, 'ফাতেমা আবেদীন, ক্যানভাস', 'গরুর চাপ কাবাব_1536157621.jpg', '2018-09-05 18:27:01', '2018-09-05 18:27:01'),
(9, 'নারকেলের দুধে খাসির রেজালা', NULL, NULL, NULL, '১. খাসির মাংস ১ কেজি,\r\n২. পেঁয়াজ কুচি ১ কাপ,\r\n৩. আদা বাটা ২ টেবিল-চামচ,\r\n৪. রসুন ১ চা-চামচ,\r\n৫. এলাচি ৪টি,\r\n৬. দারুচিনি ২ টুকরা,\r\n৭. দই ১ কাপ,\r\n৮. তেল বা ঘি আধা কাপ,\r\n৯. চিনি ১ টেবিল-চামচ,\r\n১০. লবণ পরিমাণমতো,\r\n১১. নারকেলের দুধ দেড় কাপ,\r\n১২. দুধ (রুচি অনুসারে),\r\n১৩. কাঁচা মরিচ ৫টি,\r\n১৪. নারকেল কুচি ২ টেবিল-চামচ।', '> পেঁয়াজ বাদামি করে ভেজে এর অর্ধেকটা অন্য পাত্রে তুলে রাখতে হবে। এরপর তেলে মাংস দিয়ে কিছুক্ষণ ভেজে দুধ, কাঁচা মরিচ, নারকেল কুচি ছাড়া বাকি সব মসলা দিয়ে মাংস ভালোভাবে কষিয়ে নিতে হবে। মাংস কিছুটা নরম হয়ে এলে দুধ মিশিয়ে অপেক্ষা করতে হবে ফুটে ওঠা পর্যন্ত। মাংস মাখা-মাখা হয়ে এলে তাতে নারকেল কুচি, কাঁচা মরিচ দিয়ে অল্প আঁচে চল্লিশ মিনিট রাখতে হবে। তারপর নামিয়ে নিয়ে পরিবেশন করুন। ইচ্ছে হলে উপরে বেরেস্তাও দিতে পারেন, তবে এতে বেরেস্তার কড়া ঘ্রাণে নারকেলের ঘ্রাণ হারিয়ে যাওয়ার আশঙ্কা থাকে।', NULL, 'ফাতেমা আবেদীন, ক্যানভাস', ' দুধে খাসির রেজালা_1536389813.jpg', '2018-09-08 10:56:54', '2018-09-08 10:56:54'),
(10, 'মাংসের তক্তি পিঠা', NULL, NULL, NULL, '১. হাড় ছাড়া কষানো মাংস ১ কাপ (ঝুরি করে নেয়া),\r\n২. পেঁয়াজ কুচি ১ কাপ,\r\n৩. রসুন কুচি ১ টেবিল চামচ,\r\n৪. কাঁচা মরিচ কুচি পছন্দমতো,\r\n৫. ধনিয়াপাতা কুচি ২ টেবিল-চামচ,\r\n৬. জিরা গুঁড়া ১ চা-চামচ,\r\n৭. তেল আধা কাপ,\r\n৮. ডিম ১টি,\r\n৯. চালের গুঁড়া ২ কাপ।', '> উপরের সব উপকরণ ভালোমতো মেখে কলাপাতায় মুড়ে পাতলা বিস্কুটের শেপ দিয়ে তাওয়ায় উচ্চ তাপে ভাজতে হবে। গ্রামে সরাসরি চুলার আগুনে দেয়া হয়। ইচ্ছে হলে আভেনে ২২০ ডিগ্রি তাপমাত্রায় বেক করতে পারেন। সে ক্ষেত্রে কলাপাতা লাগবে না। বিস্কুটের মতো ভাজা ভাজা হয়ে এলে কেটে সরিষার চাটনি বা কাসুন্দি দিয়ে পরিবেশন করুন।', NULL, 'ফাতেমা আবেদীন, ক্যানভাস', ' তক্তি পিঠা_1536409411.jpg', '2018-09-08 16:23:33', '2018-09-08 16:23:33'),
(11, 'কাটা মসলায় খাসির মাংস', NULL, NULL, NULL, '১. খাসির মাংস এক কেজি,\r\n২. আদা কুচি দুই চা চামচ,\r\n৩. রসুন কুচি দুই চা চামচ,\r\n৪. পেঁয়াজ কুচি এক কাপ,\r\n৫. আস্ত জিরা এক চা চামচ,\r\n৬. মরিচ গুঁড়া এক টেবিল চামচ,\r\n৭. হলুদ গুঁড়ো এক টেবিল চামচ,\r\n৮. দারুচিনি তিন টুকরা,\r\n৯. এলাচ তিনটি,\r\n১০. লবঙ্গ তিনটি,\r\n১১. আস্ত গোলমরিচ ছয়-সাতটি,\r\n১২. তেল পরিমাণমতো,\r\n১৩. লবণ স্বাদমতো।', '> প্রথমে খাসির মাংসের সঙ্গে আদা কুচি, রসুন কুচি, পেঁয়াজ কুচি, আস্ত জিরা, মরিচ গুঁড়া, হলুদ গুঁড়া, দারুচিনি, এলাচ, লবঙ্গ, আস্ত গোলমরিচ, তেল ও লবণ মিশিয়ে মেরিনেটের জন্য এক ঘণ্টা ফ্রিজে রেখে দিন। এর পর প্যানে ঢেলে ঢাকনা দিয়ে ঢেকে মাঝারি আঁচে রান্না করুন। সেদ্ধ হয়ে এলে এবং পানি শুকিয়ে গেলে চুলা থেকে নামিয়ে বাটিতে ঢেলে গরম গরম পরিবেশন করুন কাটা মসলায় খাসির মাংস।', NULL, 'এনটিভি', ' মসলায় খাসির মাংস_1536409706.jpg', '2018-09-08 16:28:27', '2018-09-08 16:28:27'),
(13, 'জয়পুরী মাংস', NULL, NULL, NULL, '১. খাসির মাংস ১ কেজি,\r\n২. পেঁয়াজ ১ কাপ,\r\n৩. আদা বাটা দেড় টেবিল চামচ,\r\n৪. আস্ত ধনিয়া ১ টেবিল চামচ,\r\n৫. টক দই ১ কাপ,\r\n৬. বড় এলাচ ৪ টি,\r\n৭. শুকনো মরিচ কুচি (বিচি ছাড়া) ৪ টি,\r\n৮. তেজপাতা ২ টি,\r\n৯. ঘি ৩ টেবিল চামচ,\r\n১০. লবণ স্বাদমতো,\r\n১১. চিনি ১ টেবিল চামচ,\r\n১২. হলুদ ১ চা চামচ,\r\n১৩. মরিচ গুঁড়া ১ চা চামচ।', '>প্রথমে একটি কড়াইতে মাংসের সঙ্গে বাকি সব মশলা ভালো করে মাখিয়ে নিন। এরপরে ঘি দিয়ে অল্প আঁচে চুলায় বসিয়ে রাখুন। এবার মাংসের উপরে তেল ভেসে উঠলে দই দিয়ে দিন। এরপরে ভালো করে নাড়ুন।মাখা মাখা হলে নামিয়ে ফেলুন।এবার গরম গরম পরিবেশন করুন মজাদার জয়পুরী মাংস।', NULL, 'এনটিভি', ' মাংস_1536409949.jpg', '2018-09-08 16:32:29', '2018-09-08 16:32:29'),
(14, 'খাসির স্টেক', NULL, NULL, NULL, '১. খাসির মাংস ১ কেজি,\r\n২. মরিচ গুঁড়া ১ চা চামচ,\r\n৩. লবণ পরিমাণমতো,\r\n৪. সয়া সস ২ টেবিল চামচ,\r\n৫. তেল ৬ টেবিল চামচ,\r\n৬. ঘি ২ টেবিল চামচ,\r\n৭. শসা ১টি,\r\n৮. গাজর ১ টি,\r\n৯. টমেটো ১ টি।', '> প্রথমে একটি পাত্রে মাংস টুকরা করে মরিচ গুঁড়া, লবণ, সয়া সস দিয়ে মেখে ম্যারিনেট করতে হবে ১ ঘণ্টা। বেকিং ট্রে তে তেল ঢেলে ছড়িয়ে দিতে হবে যেন কোনো অংশ বাদ না পড়ে। ওভেনে ২৫০ তাপে বা ম্যাক্স এ দিয়ে ট্রে সহ প্রিহিট করতে হবে ৫ মিনিট। তেল গরম হয়ে এলে ট্রে বের করে মাংস সাজিয়ে ওভেনে ১০ মিনিটের জন্য দিতে হবে। তারপর ৭ মিনিটের মাথায় ট্রে বের করে মাংস উল্টিয়ে ঘি দিয়ে ব্রাশ করে উপরের তাকে ৩ মিনিট এর জন্য দিতে হবে। এরপর নামিয়ে শসা, গাজর টমেটো দিয়ে সাজিয়ে পরিবেশন করুন সুস্বাদু খাসির স্টেক।', NULL, 'আরটিভি', ' স্টেক_1536410039.jpg', '2018-09-08 16:34:00', '2018-09-08 16:34:00'),
(15, 'কড়াই মাটন', NULL, NULL, NULL, '১. খাসির মাংস ১০০০ গ্রাম,\r\n২. টমেটো টুকরো আধা কাপ,\r\n৩. টক দই আধা কাপ,\r\n৪. কাঁচামরিচ কুচি ২-৩ টি,\r\n৫. পেঁয়াজ কুচি ১ কাপ,\r\n৬. হলুদ গুঁড়া ১ চা চামচ,\r\n৭. রসুন বাটা ১ চা চামচ,\r\n৮. আদা বাটা ১ চা চামচ,\r\n৯. মরিচ গুঁড়া ১ চা চামচ,\r\n১০. তেল ৩ টেবিল চামচ,\r\n১১. লবণ স্বাদ মতো,\r\n১২. শুকনা মরিচ ১ টেবিল চামচ,\r\n১৩. টমেটো সস ৩ টেবিল চামচ,\r\n১৪. জয়ফল গুঁড়া ১ চা চামচ।', '> প্রথমে পেঁয়াজ কুচি, তেল আর এলাচ বাদে বাকি সব উপকরণ এক সঙ্গে মেখে ২ ঘণ্টা নরমাল ফ্রিজে রেখে দিতে হবে।\r\n> এরপর কড়াইতে তেল গরম করে তাতে এলাচ ও পেঁয়াজ লালচে করে ভাজতে হবে।\r\nএরপর মাংস দিয়ে বেশ কিছুক্ষণ কষাতে হবে।পানি শুকিয়ে গেলে আবার গরম পানি দিয়ে মাখা মাখা হওয়া পর্যন্ত রান্না করতে হবে।\r\n> মাংসের ওপরে তেল উঠে আসলে নামিয়ে ফেলুন।', NULL, 'আরটিভি', ' মাটন_1536637786.jpg', '2018-09-11 07:49:47', '2018-09-11 07:49:47'),
(16, 'খাসির পায়ের তন্দুরি', NULL, NULL, NULL, '১. ১ কেজি পরিমান হাড়সহ খাসির মাংস,\r\n২. ১৭৫ গ্রাম টক দই,\r\n৩. জিড়া গুঁড়া ১ চা চামচ,\r\n৪. গরম মসলার গুঁড়া ১ চা চামচ,\r\n৫. ধনিয়া পাতা বাটা ১ চা চামচ,\r\n৬. হলুদ গুঁড়া আধা চা চামচ,\r\n৭. লাল মরিচের গুঁড়া ১ চা চামচ,\r\n৮. স্বাদ অনুযায়ী লবণ,\r\n৯. লেবুর রস ২ চা চামচ,\r\n১০. আদা বাটা ১ চা চামচ,\r\n১১. রসুন বাটা ১ চা চামচ।', '> প্রথমে হাড়সহ মাংসগুলোতে লবণ, আদা বাটা আর রসুন দিয়ে ভালোভাবে মাখিয়ে ২০ মিনিট রেখে দিন। এবার মেরিনেটেড মাংসের সাথে অন্যান্য সব উপকরণ মিশিয়ে পুরো বাটিটা ভালো করে ঢেকে আরো ৩০-৬০ মিনিট রেখে দিন। এবার বাটি থেকে মাংসগুলো স্টিকে গেঁথে গরম কয়লার চুলার উপর রেখে ধীরে ধীরে রান্না করুন।', NULL, 'অর্থসূচক', ' পায়ের তন্দুরি_1536640923.jpg', '2018-09-11 08:42:04', '2018-09-11 08:42:04'),
(17, 'খাসির ঝাল-মাংস', NULL, NULL, NULL, '১. খাসির মাংস এক কেজি,\r\n২. টকদই আধা কাপ,\r\n৩. পেঁয়াজের কুচি পাঁচ টেবিল চামচ,\r\n৪. মরিচের গুঁড়া দুই টেবিল চামচ,\r\n৫. হলুদের গুঁড়া দুই টেবিল চামচ,\r\n৬. আদা বাটা এক টেবিল চামচ,\r\n৭. রসুন বাটা এক টেবিল চামচ,\r\n৮. ধনিয়া গুঁড়া এক চা চামচ,\r\n৯. জিরা গুঁড়া এক চা চামচ,\r\n১০. আলু ছোট করে কাটা ৮-১০টি,\r\n১১. গরম মসলার গুঁড়া এক চা চামচ,\r\n১২. কাঁচামরিচ সাত-আটটি,\r\n১৩. তেল পরিমাণমতো,\r\n১৪. লবণ স্বাদমতো।', '> প্রথমে একটি বাটিতে খাসির মাংস, টকদই, আদা বাটা, রসুন বাটা ও লবণ একসঙ্গে মেখে মেরিনেটের জন্য এক ঘণ্টা রেখে দিন। এবার একটি প্যানে তেল গরম করে পেঁয়াজের কুচি, মরিচের গুঁড়ো, হলুদের গুঁড়া, ধনিয়া গুঁড়া, জিরা গুঁড়া, গরম মসলার গুঁড়া ও লবণ দিয়ে কষাতে থাকুন। এখন মেরিনেট করা মাংসগুলো দিয়ে কষাতে থাকুন। তেল উঠে এলে পানি দিন। ঢাকনা দিয়ে ঢেকে অল্প আঁচে রান্না করুন। এবার এতে আলু ও কাঁচামরিচ দিয়ে নেড়ে আবারও ঢাকনা দিয়ে ঢেকে দিন। মাংস সেদ্ধ হয়ে গেলে চুলা থেকে নামিয়ে বাটিতে ঢেলে গরম গরম পরিবেশন করুন খাসির ঝাল-মাংস।', NULL, 'এনটিভি', ' ঝাল-মাংস_1536641063.jpg', '2018-09-11 08:44:24', '2018-09-11 08:44:24'),
(18, 'খাসির আচারি মাংস', NULL, NULL, NULL, '১. খাসির মাংস এক কেজি,\r\n২. আদা বাটা এক চা চামচ,\r\n৩. রসুন বাটা এক চা চামচ,\r\n৪. পেঁয়াজ কুচি পাঁচ টেবিল চামচ,\r\n৫. মরিচের গুঁড়া এক চা চামচ,\r\n৬. হলুদের গুঁড়া এক চা চামচ,\r\n৭. গরম মসলার গুঁড়া এক চা চামচ,\r\n৮. আমের আচার তিন টেবিল চামচ,\r\n৯. জলপাইয়ের আচার তিন টেবিল চামচ,\r\n১০. তেল পরিমাণমতো,\r\n১১. লবণ স্বাদমতো।', '> প্রথমে একটি বাটিতে খাসির মাংস, আদা বাটা, রসুন বাটা, মরিচের গুঁড়া, হলুদের গুঁড়া, গরম মসলার গুঁড়া, তেল ও লবণ দিয়ে ভালো করে মিশিয়ে মেরিনেটের জন্য ১ ঘণ্টা ফ্রিজে রেখে দিন। এবার প্যানে তেল গরম করে তাতে পেঁয়াজ কুচি দিয়ে ভাজতে থাকুন। বাদামি হয়ে এলে মেরিনেট করা মাংস দিয়ে কষাতে থাকুন। তেল উঠে এলে পানি মিশিয়ে ঢাকনা দিয়ে ঢেকে দিন। মাংস সেদ্ধ হলে এর মধ্যে আমের ও জলপাইয়ের আচার দিয়ে পাঁচ মিনিট রান্না করুন। এবার চুলা থেকে নামিয়ে বাটিতে ঢেলে গরম গরম পরিবেশন করুন খাসির আচারি মাংস।', NULL, 'এনটিভি', ' আচারি মাংস_1536641199.jpg', '2018-09-11 08:46:39', '2018-09-11 08:46:39'),
(19, 'খাসির কোরমা', NULL, NULL, NULL, '১. খাসির মাংস এক কেজি,\r\n২. পেঁয়াজ কুচি এক কাপ,\r\n৩. আদা বাটা দুই চা চামচ,\r\n৪. রসুন বাটা দুই চা চামচ,\r\n৫. দারুচিনি তিন টুকরা,\r\n৬. এলাচ তিনটি,\r\n৭. লবঙ্গ তিনটি,\r\n৮. টক দই আধা কাপ,\r\n৯. লেবুর রস দুই চা চামচ,\r\n১০. দুধ আধা কাপ,\r\n১১. ঘি পরিমাণমতো,\r\n১২. চিনি স্বাদমতো,\r\n১৩. কাঁচামরিচ ১০-১২টি,\r\n১৪. লবণ স্বাদমতো।', '> প্রথমে পেঁয়াজ কুচি তেলে ভেজে বেরেস্তা করে নিন। এবার একটা প্যানে খাসির মাংস, আদা বাটা, রসুন বাটা, দারুচিনি, এলাচ, লবঙ্গ, টক দই, লেবুর রস, দুধ, ঘি ও লবণ দিয়ে নেড়ে মাঝারি আঁচে কষাতে থাকুন। তেল উঠে এলে এতে পানি দিয়ে ঢেকে দিন। মাংস সেদ্ধ হয়ে এলে এতে পেঁয়াজ বেরেস্তা, কাঁচামরিচ ও চিনি দিয়ে নেড়ে আরো কিছুক্ষণ দমে রাখুন। ব্যস, খুব সহজেই তৈরি হয়ে গেল সুস্বাদু খাসির কোরমা।', NULL, 'এনটিভি', ' কোরমা_1536641330.jpg', '2018-09-11 08:48:51', '2018-09-11 08:48:51'),
(20, 'কাটা মসলার মাংস', NULL, NULL, NULL, '১. মাংসের টুকরা ২ কেজি,\r\n২. পেঁয়াজ টুকরা ২ কাপ,\r\n৩. আদা মিহিকুচি ২ টেবিল-চামচ,\r\n৪. রসুনকুচি দেড় চা-চামচ,\r\n৫. শুকনামরিচ ১২টি। এলাচ ৮টি,\r\n৬. দারুচিনি ২ সে.মি. ৬ টুকরা,\r\n৭. তেজপাতা ২টি,\r\n৮. গোলমরিচ ১ চা-চামচ,\r\n৯. তেল এককাপ এবং এককাপের চারভাগের একভাগ,\r\n১০. সিরকা আধাকাপ বা টক দই ১ কাপ,\r\n১১. লবণ স্বাদমতো।', '> গরুর সিনার এবং রানের মাংস চর্বি ছাড়িয়ে টুকরা করে কেটে, ধুয়ে পানি ঝরিয়ে নিন।\r\n> হাঁড়িতে মাংস এবং সব উপকরণ মেশান। ঢাকনা দিয়ে মৃদু আঁচে দুই থেকে তিন ঘণ্টা রান্না করুন।\r\n> মাংস সিদ্ধ হলে এবং পানি শুকালে খুব মৃদু আঁচে একঘণ্টা দমে রেখে দিন। \r\n> নামিয়ে পরিবেশন করুন। এখানে প্রায় ১২ থেকে ১৪ জনের জন্য হবে।', NULL, 'সিদ্দিকা কবীর, তৈরি করেছেন রোয়েনা মাহজাবিন, বিডিনিউজ টোয়েন্টিফোর ডটকম', ' মসলার মাংস_1536641506.jpg', '2018-09-11 08:51:47', '2018-09-11 08:51:47'),
(21, 'কলিজা ভুনা', NULL, NULL, NULL, '১. গরু অথবা খাসির কলিজা ৫০০ গ্রাম,\r\n২. পেঁয়াজ কুচি দুই টেবিল চামচ,\r\n৩. আদা-রসুন বাটা দুই চা চামচ,\r\n৪. শুকনা মরিচ তিনটি,\r\n৫. হলুদের গুঁড়া আধা চা চামচ,\r\n৬. মরিচের গুঁড়া এক চা চামচ,\r\n৭. জিরা গুঁড়া আধা চা চামচ,\r\n৮. গরম মসলার গুঁড়া আধা চা চামচ,\r\n৯. তেল পরিমাণমতো,\r\n১০. লবণ স্বাদমতো।', '> প্রথমে কলিজা ভালো করে ধুয়ে পরিষ্কার করে ১০ মিনিট পানিতে সেদ্ধ করুন। এবার সেদ্ধ করা কলিজা একটি বাটিতে নিয়ে এর মধ্যে আদা-রসুন বাটা, শুকনা মরিচ, হলুদের গুঁড়া, মরিচের গুঁড়া, জিরা গুঁড়া, সামান্য তেল ও লবণ দিয়ে ভালো করে মিশিয়ে মেরিনেটের জন্য ৩০ মিনিট রেখে দিন। এবার একটি প্যানে তেল দিয়ে তাতে পেঁয়াজ কুচি দিয়ে ভাজতে থাকুন। পেঁয়াজ বাদামি হয়ে গেলে এর মধ্যে মেরিনেট করা কলিজা দিয়ে কষাতে থাকুন। কষানো হয়ে গেলে অল্প পানি দিয়ে ঢাকনা দিয়ে ঢেকে দিন। পানি শুকিয়ে গেলে গরম মসলার গুঁড়া দিয়ে নেড়ে চুলা থেকে নামিয়ে বাটিতে ঢেলে গরম গরম পরিবেশন করুন কলিজা ভুনা।', NULL, 'এনটিভি', ' ভুনা_1536641603.jpg', '2018-09-11 08:53:24', '2018-09-11 08:53:24'),
(22, 'গরুর মাংসের কাঠি কাবাব', NULL, NULL, NULL, '১. গরুর মাংসের কিমা আধা কেজি,\r\n২. ডিম একটি,\r\n৩. পেঁয়াজ বেরেস্তা দুই টেবিল চামচ,\r\n৪. আদা বাটা এক চা চামচ,\r\n৫. রসুন বাটা এক চা চামচ,\r\n৬. ঘি দুই টেবিল চামচ,\r\n৭. কর্নফ্লাওয়ার এক টেবিল চামচ,\r\n৮. কাঁচামরিচ কুচি চার-পাঁচটি,\r\n৯. জয়ফল গুঁড়া আধা চা চামচ,\r\n১০. জয়ত্রী গুঁড়া আধা চা চামচ,\r\n১১. কাজুবাদাম বাটা দুই টেবিল চামচ,\r\n১২. কাঁচা পেঁপে বাটা দুই চা চামচ,\r\n১৩. লবণ স্বাদমতো,\r\n১৪. কাঠি পরিমাণমতো।', '> প্রথমে একটি বাটিতে গরুর মাংসের কিমা, ডিম, পেঁয়াজ বেরেস্তা, আদা বাটা, রসুন বাটা, ঘি, কর্নফ্লাওয়ার, কাঁচামরিচ কুচি, জয়ফল গুঁড়া, জয়ত্রী গুঁড়া, কাজুবাদাম, কাঁচা পেঁপে ও লবণ একসঙ্গে ভালো করে মিশিয়ে নিন। এবার মেরিনেটের জন্য মাংসের মিশ্রণ এক ঘণ্টা ফ্রিজে রেখে দিন। এরপর কাঠের কাঠির মধ্যে মুঠো করে মাংসের মিশ্রণ লাগিয়ে নিন। প্যানে ঘি গরম করে এগুলো ভাজতে থাকুন। দুই পাশ বাদামি হয়ে গেলে চুলা থেকে নামিয়ে প্লেটে সাজিয়ে গরম গরম পরিবেশন করুন গরুর মাংসের কাঠি কাবাব।', NULL, 'এনটিভি', ' মাংসের কাঠি কাবাব_1536641698.jpg', '2018-09-11 08:54:59', '2018-09-11 08:54:59'),
(23, 'গরুর মাংসের শিক কাবাব', NULL, NULL, NULL, '১. হাড় ছাড়া গরুর মাংস এক কেজি,\r\n২. আদা বাটা এক চা চামচ,\r\n৩. রসুন বাটা এক চা চামচ,\r\n৪. জিরা গুঁড়া এক চা চামচ,\r\n৫. মরিচের গুঁড়া এক চা চামচ,\r\n৬. ধনিয়া গুঁড়া এক চা চামচ,\r\n৭. গরম মসলা গুঁড়া এক চা চামচ,\r\n৮. কাঁচামরিচ কুচি চার/পাঁচটি,\r\n৯. নারিকেল কুচি দুই টেবিল চামচ,\r\n১০. ধনিয়পাতা কুচি দুই টেবিল চামচ,\r\n১১. লবণ স্বাদমতো।', '> প্রথমে একটি ব্লেন্ডারে হাড় ছাড়া গরুর মাংস, আদা বাটা, রসুন বাটা, জিরা গুঁড়া, মরিচের গুঁড়া, ধনিয়া গুঁড়া, গরম মসলা গুঁড়া, কাঁচামরিচ কুচি, নারকেল কুচি, ধনিয়াপাতা কুচি ও লবণ দিয়ে ভালো করে ব্লেন্ড করুন। পানি দেবেন না। এবার ব্লেন্ড করা এই মিশ্রণ মেরিনেটের জন্য এক ঘণ্টা ফ্রিজে রেখে দিন। এর পর মাংসের মিশ্রণ শিকে গেঁথে কয়লার চুলায় উল্টেপাল্টে সেঁকে নিন। ব্যস, খুব সহজেই তৈরি হয়ে গেল সুস্বাদু শিক কাবাব।', NULL, 'এনটিভি', ' মাংসের শিক কাবাব_1536641838.jpg', '2018-09-11 08:57:19', '2018-09-11 08:57:19'),
(24, 'আফগানি বিফ কাবাব', NULL, NULL, NULL, '১. গরুর মাংস এক কেজি,\r\n২. আদা বাটা এক চা চামচ,\r\n৩. রসুন বাটা এক চা চামচ,\r\n৪. পেঁয়াজ বাটা এক টেবিল চামচ,\r\n৫. কালো গোলমরিচের গুঁড়া সামান্য,\r\n৬. সাদা গোলমরিচের গুঁড়া সামান্য,\r\n৭. অলিভ অয়েল দুই টেবিল চামচ,\r\n৮. গরম মসলা গুঁড়া আধা চা চামচ,\r\n৯. লেবুর রস এক টেবিল চামচা,\r\n১০. লবণ পরিমাণমতো।', '> প্রথমে একটি ব্লেন্ডারে পেঁয়াজ, আদা, রসুন, লেবুর রস ও অলিভ অয়েল একসঙ্গে মিশিয়ে ভালো করে ব্লেন্ড করুন। এবার একটি বাটিতে মাংস, ব্লেন্ড করা মসলা, কালো গোলমরিচের গুঁড়া, সাদা গোলমরিচের গুঁড়া, গরম মসলা গুঁড়া ও লবণ নিয়ে ভালো করে মিশিয়ে মেরিনেটের জন্য এক ঘণ্টা ফ্রিজে রেখে দিন। এবার মেরিনেট করা মাংস শিকে গেঁথে কয়লার আগুনে পুড়ে কাবাব তৈরি করে নিন। এবার পুদিনা পাতার সসের সঙ্গে গরম গরম পরিবেশন করুন মজাদার আফগানি বিফ কাবাব।', NULL, 'এনটিভি', ' বিফ কাবাব_1536641904.jpg', '2018-09-11 08:58:25', '2018-09-11 08:58:25'),
(25, 'বিফ বটি কাবাব', NULL, NULL, NULL, '১. গরুর মাংস এক কেজি,\r\n২. মরিচের গুঁড়া দুই চা চামচ,\r\n৩. জিরা গুঁড়া এক চা চামচ,\r\n৪. পেঁয়াজ বাটা এক চা চামচ,\r\n৫. ধনিয়া গুঁড়া এক চা চামচ,\r\n৬. আদা বাটা এক চা চামচ,\r\n৭. রসুন বাটা এক চা চামচ,\r\n৮. গরম মসলা গুঁড়া আধা চা চামচ,\r\n৯. টক দই চার টেবিল চামচ,\r\n১০. সরিষা বাটা আধা চা চামচ,\r\n১১. ক্রিম তিন চা চামচ,\r\n১২. সরিষার তেল চার টেবিল চামচ,\r\n১৩. শুকনো মরিচ ভাজা গুঁড়া এক চা চামচ,\r\n১৪. কাঁচা পেঁপে বাটা দুই টেবিল চামচ,\r\n১৫. লবণ স্বাদমতো।', '> প্রথমে গরুর মাংস ছোট ছোট কিউব করে কেটে নিন। এবার ভালো করে ধুয়ে একটি ছাঁকনিতে রাখুন, যাতে পানি পুরোপুরি ঝরে যায়। এবার একটি বাটিতে গরুর মাংস, মরিচের গুঁড়া, জিরা গুঁড়া, পেঁয়াজ বাটা, ধনিয়া গুঁড়া, আদা বাটা, রসুন বাটা, গরম মসলা গুঁড়া, টক দই, সরিষা বাটা, ক্রিম, সরিষার তেল, শুকনো মরিচ ভাজা গুঁড়া, কাঁচা পেঁপে বাটা ও লবণ নিয়ে ভালো করে মিশিয়ে মেরিনেটের জন্য এক ঘণ্টা ফ্রিজে রেখে দিন। এবার মাংসগুলো শিকে গেঁথে গ্রিলে দিন। একপাশ হয়ে গেলে সরিষার তেল দিয়ে অন্য পাশ গ্রিল করে নিন। ব্যস, তৈরি হয়ে গেল সুস্বাদু বিফ বটি কাবাব।', NULL, 'এনটিভি', ' বটি কাবাব_1536641992.jpg', '2018-09-11 08:59:53', '2018-09-11 08:59:53'),
(26, 'বিফ কোপ্তা কাবাব', NULL, NULL, NULL, '১. গরুর মাংসের কিমা আধা কেজি,\r\n২. পেঁয়াজ বাটা দুই টেবিল চামচ,\r\n৩. আদা বাটা আধা চা চামচ,\r\n৪. রসুন বাটা আধা আধা চা চামচ,\r\n৫. গরম মসলা গুঁড়া এক চা চামচ,\r\n৬. সরিষার তেল আধা কাপ,\r\n৭. কাঁচামরিচ কুচি চার/পাঁচটি,\r\n৮. ধনিয়াপাতা কুচি দুই টেবিল চামচ,\r\n৯. লবণ স্বাদমতো।', '> প্রথমে একটি বাটিতে গরুর মাংসের কিমা, পেঁয়াজ বাটা, আদা বাটা, রসুন বাটা, গরম মসলা গুঁড়া, সরিষার তেল, কাঁচামরিচ কুচি, ধনিয়াপাতা কুচি ও লবণ নিয়ে একসঙ্গে ভালো করে মিশিয়ে গোল গোল বল তৈরি করুন। এবার একটি প্লেটে এই বলগুলো নিয়ে মেরিনেটের জন্য এক ঘণ্টা ফ্রিজে রেখে দিন। এখন একটি প্যানে তেল গরম করুন। এবার ডুবো তেলে কোপ্তাগুলো ভেজে নিন। বাদামি হয়ে গেলে কোপ্তাগুলো তেল থেকে তুলে কাঠের কাঠির মধ্যে গেঁথে পরিবেশন করুন।', NULL, 'এনটিভি', ' কোপ্তা কাবাব_1536642169.jpg', '2018-09-11 09:02:50', '2018-09-11 09:02:50'),
(27, 'বিফ কাবাব', NULL, NULL, NULL, '১. গরুর মাংস ৫০০ গ্রাম,\r\n২. পেঁয়াজ কুচি বেরেস্তা ৩ চা চামচ,\r\n৩. আদা কুচি ১ চা চামচ,\r\n৪. শুকনা মরিচ ১ চা চামচ,\r\n৫. দারুচিনি ২টি, এলাচ ৩টি,\r\n৬. হলুদ গুঁড়া ১ চা চামচ,\r\n৭. টালা জিরা গুঁড়া ১ চা চামচ,\r\n৮. লবণ স্বাদমতো,\r\n৯. টমেটো সস ১ টেবিল চামচ,\r\n১০. লেবুর রস ১ টেবিল চামচ,\r\n১১. পাউরুটি ৩ টুকরা,\r\n১২. ডিম ১টি,\r\n১৩. তেল আধা কাপ।', '> প্রথমে একটি পাত্রে মাংস ও বাকি সব মশলা দিয়ে ভালো করে কষিয়ে নিতে হবে।এবার মাংস সেদ্ধ হয়ে এলে বেটে নিতে হবে।\r\n> তারপর পাউরুটি ভিজিয়ে পানি ঝরিয়ে মাংসের সঙ্গে মিশিয়ে নিতে হবে।\r\n> এরপর লেবুর রস, পেঁয়াজ বেরেস্তা আর ডিম দিয়ে ভালোভাবে মেখে গোলা করে ডুবোতেলে ভেজে নিতে হবে।\r\n> এবার গরম গরম পরিবেশন করুন মজাদার বিফ কাবাব।', NULL, 'আরটিভি', ' কাবাব_1536642306.jpg', '2018-09-11 09:05:07', '2018-09-11 09:05:07'),
(28, 'বিফ কোকোনাট', NULL, NULL, NULL, '১. গরুর মাংস ৫০০ গ্রাম,\r\n২. নারিকেল বাটা ৩ টেবিল চামচ,\r\n৩. পেস্তা বাদাম বাটা ১ চা চামচ,\r\n৪. কিশমিশ বাটা ১ চা চামচ,\r\n৫. কাজু বাদাম বাটা ১ চা চামচ,\r\n৬. জিরা গুঁড়া ১ চা চামচ,\r\n৭. কাঁচামরিচ ২-৩ টি,\r\n৮. পেঁয়াজ কুচি ১ কাপ,\r\n৯. রসুন বাটা ১ চা চামচ,\r\n১০. আদা বাটা ১ চা চামচ,\r\n১১. কালোজিরা ১ চা চামচ,\r\n১২. ধনিয়া গুঁড়া ২ চা চামচ,\r\n১৩. মরিচ গুঁড়া ১ চা চামচ,\r\n১৪. হলুদ গুঁড়া ১ চা চামচ,\r\n১৫. গরম মসলা গুঁড়া ১ চা চামচ,\r\n১৬. তেল ৩ টেবিল চামচ,\r\n১৭. চিনি ১ চা চামচ,\r\n১৮. লবণ স্বাদমতো।', '> প্রথমে একটি পাত্রে তেল গরম করে পেঁয়াজ বাদামি করে ভেজে নিন।\r\n> এরপর গরুর মাংস, আদা বাটা, রসুন বাটা, মরিচ গুঁড়া, জিরা গুঁড়া, হলুদ গুঁড়া, ধনিয়া গুঁড়া ও লবণ একসঙ্গে মেখে ভালোভাবে কষিয়ে নিন।\r\n> তারপর পরিমাণমতো গরম পানি দিয়ে আরো কিছুক্ষণ আঁচ বাড়িয়ে রান্না করুন।\r\n> পানি শুকিয়ে এলে কাজুবাদাম, গরম মসলা, চিনি, পেস্তা বাটা আর নারিকেল বাটা দিয়ে আরো কিছুক্ষণ রান্না করুন।\r\n> মাংসের ওপরে তেল উঠে আসলে নামিয়ে ফেলুন। এবার গরম গরম পরিবেশন করুন সুস্বাদু বিফ কোকোনাট।', NULL, 'আরটিভি', ' কোকোনাট_1536642412.jpg', '2018-09-11 09:06:53', '2018-09-11 09:06:53'),
(29, 'জালি কাবাব', NULL, NULL, NULL, 'গরুর মাংসের কিমা ৫০০ গ্রাম,\r\nডিম ৬ টি,\r\nটোস্ট বিস্কুটের গুঁড়া ২ টেবিল চামচ,\r\nপাউরুটি ৪ পিস,\r\nকাবাব মশলা ১ টেবিল চামচ,\r\nকাঁচামরিচ কুচি ১ টেবিল চামচ,\r\nধনিয়াপাতা কুচি ৪ টেবিল চামচ,\r\nপেঁয়াজ কিউব করে কুচি ৪ টেবিল চামচ,\r\nপেঁপে বাটা ২ টেবিল চামচ,\r\nলবণ স্বাদ মতো,\r\nটমেটো সস ১ টেবিল চামচ,\r\nটেস্টিং সল্ট ১ চা চামচ।', '> প্রথমে পাউরুটির শক্ত কিনার ফেলে পানিতে ভিজিয়ে রাখতে হবে।নরম হয়ে এলে পানি ঝরিয়ে নিতে হবে।\r\n> এরপর মাংসের কিমা, পাউরুটি, কাঁচামরিচ কুচি, পেঁয়াজ কুচি, ধনেপাতা কুচি, ডিম ২ টি, কাবাব মশলা, পেঁপে বাটা এবং টমেটো সস একসঙ্গে ভালোকরে মাখাতে হবে।\r\n> এবার মিশ্রণটি রেফ্রিজারেটরে ১ ঘণ্টা রেখে দিতে হবে।তারপর মিশ্রণটি নামিয়ে এতে বিস্কুটের গুঁড়া, লবণ এবং টেস্টিং সল্ট দিয়ে আবার মাখাতে হবে।\r\n> এইবার মাখানো কিমা দুই হাতে ছোট ছোট গোল করে কাবাবের আকার তৈরি করে টোস্ট বিস্কুটের গুঁড়ায় গড়িয়ে অন্য একটি পাত্রে আবার ফ্রিজে ২০ মিনিটের জন্য রেখে দিতে হবে।\r\n> একটা পাত্রে ৪ টি ডিম ১ চিমটি লবণ দিয়ে ফেটিয়ে নিতে হবে।কাবাবগুলো ফেটানো ডিমে ডুবিয়ে কড়াইয়ে ডুবোতেলে ভাজতে হবে।বাদামী রঙ ধারণ করলে চুলা থেকে নামিয়ে তেল ঝরিয়ে গরম গরম পরিবেশন করুন সুস্বাদু জালি কাবাব।', NULL, 'আরটিভি', ' কাবাব_1536642499.jpg', '2018-09-11 09:08:20', '2018-09-11 09:08:20'),
(30, 'নেহারি', NULL, NULL, NULL, 'খাসির বা গরুর পায়া (পা) আধা কেজি।\r\nমাংস হাড়সহ আধা কেজি।\r\nপেঁয়াজকুচি ১/৪ কাপ।\r\nআদাবাটা ১ চা-চামচ,\r\nরসুনবাটা ১ চা-চামচ,\r\nটমেটো ২টি কুচি করা,\r\nকাঁচামরিচ ৩/৪টি,\r\nএলাচ ৪টি। লবঙ্গ ৪টি,\r\nগোলমরিচ ৪/৫টি,\r\nতেজপাতা ২টি,\r\nদারুচিনি ২ টুকরা,\r\nহলুদগুঁড়া আধা চা-চামচ,\r\nমরিচগুঁড়া ১ চা-চামচ,\r\nজিরার গুঁড়া ১ চা-চামচ,\r\nআধা চামচ গরম মসলার গুঁড়া,\r\nটক দই আধা কাপ,\r\nধনিয়াপাতা ১/৪ কাপ,\r\nআদাকুচি ১ টেবিল-চামচ,\r\nলবণ স্বাদমতো,\r\nতেল ২ চা-চামচ।', '> পায়া ও মাংস পরিষ্কার করে টুকরা করুন।\r\n> এবার যে পাতিলে নেহারি রান্না করবেন সেটাতে তেল গরম করে পেঁয়াজ, কাঁচামরিচ, টমেটো-কুচি ভাজুন। একটু নরম হলে নামিয়ে ঠাণ্ডা করুন। তারপর ব্লেন্ড করে আবার পাতিলে ঢেলে আদা ও রসুন বাটা, এলাচি, দারুচিনি, লবঙ্গ, গোলমরিচ, হলুদ, মরিচ, জিরার গুঁড়া দিয়ে পায়া ও মাংস একসঙ্গে হালকা লাল করে ভেজে নিন।\r\n> এবার মাংস ডুবিয়ে বেশি করে পানি দিন। অর্থাৎ প্রায় দেড় থেকে দুই গুন পানি দিয়ে সিদ্ধ করুন। লবন দিন। তিন থেকে চার ঘন্টা অল্প আঁচে রান্না করুন। ঝোল কমে একটু ঘন হয়ে আসলে দই ভালো করে ফেটে ঝোলের সঙ্গে মেশান।\r\n> হাড় থেকে মাংস খুলে আসলে গরম মসলার গুঁড়া দিয়ে নেড়ে তারপর নামিয়ে আনুন।\r\n> পরিবেশনের আগে পেঁয়াজ-বেরেস্তা ও ধনিয়াপাতা দিয়ে সাজিয়ে গরম গরম পরিবেশন করুন।', NULL, 'তাসনুভা রোজ নওরিন', '_1536642602.jpg', '2018-09-11 09:10:02', '2018-09-11 09:10:02'),
(31, 'দম কাবাব', NULL, NULL, NULL, '১. মাংস বাটা আদা কেজি,\r\n২. আদা বাটা আদা চা-চামচ,\r\n৩. রসুন বাটা ৪ ভাগের এক চা-চামচ,\r\n৪. হলুদ বাটা ৪ ভাগের এক চা-চামচ,\r\n৫. মরিচ বাটা আধা চা-চামচ,\r\n৬. জিরা বাটা আধা চা-চামচ,\r\n৭. ধনিয়া বাটা আধা চা-চামচ,\r\n৮. গোলমরিচ গুঁড়ো ৪ ভাগের এক চা-চামচ,\r\n৯. এলাচ বাটা ২টি,\r\n১০. দারুচিনি বাটা ২ টুকরা,\r\n১১. লবঙ্গ বাটা ১টা,\r\n১২. ডিম হালকা ফেটান একটা,\r\n১৩. টোস্টের গুঁড়ো আধা কাপ,\r\n১৪. দই বা সিরকা ১ টেবিল চামচ,\r\n১৫. ঘি ২ চা-চামচ,\r\n১৬. লবণ স্বাদমতো।', '> প্রথমে বাটা মাংসর সাথে সব মসলা ভালো ভাবে মিসিয়ে ৫-৬ ঘণ্টা রেখে দিন। এর পর একটি অ্যালোমেনিয়ামের পাত্রে ঘি মাখিয়ে মাংস দিয়ে তার উপর বাকি ঘি দিয়ে দিন এর পর একটি ঢাকনা দিয়ে ভালো ভাবে ঢেকে দিয়ে বড় একটি পাত্রে পানি দিয়ে তার উপর মাংসের পাত্রটি রেখে আবার ঢেকে দিন খেল রাখবেন পানি যেন মাংসের ভেতর চলে না যায়। এবার চুলায় আঁচ খুব কম রাখবেন। অথবা ওভেনে ১৮০সেঃ তাপে কাবাব ১ ঘণ্টা বেক করুন। মাঝে মাঝে একটু নেড়ে দেখবেন সেদ্ধ হয়েছে কিনা। ভালো ভাবে সেদ্ধ হয়ে গেলে নামিয়ে গরম গরম পরিবেশন করুন দম কাবাব।', NULL, 'পরিবর্তন.কম, ছবি : ইন্টারনেট', ' কাবাব_1536642773.jpg', '2018-09-11 09:12:54', '2018-09-11 09:12:54'),
(32, 'ভুনা কাবাব', NULL, NULL, NULL, '১. মাংস ১ কেজি,\r\n২. তেল পরিমানমতো,\r\n৩. তেজপাতা ১টি,\r\n৪. এলাচ ২টি,\r\n৫. দারচিনি ২ সেঃমি ৩ টুকরা,\r\n৬. লবঙ্গ ২টি,\r\n৭. পেয়াজ স্লাইস ২ কাপ,\r\n৮. জিরা বাটা ২ চা-চামচ,\r\n৯. জয়ত্রি বাটা কোয়াটার চা-চামচ,\r\n১০. আদা বাটা ২ চা-চামচ,\r\n১১. রসুন বাটা ২ চা-চামচ,\r\n১২. ধনিয়া বাটা ২ চা-চামচ,\r\n১৩. গোলমরিচ বাটা ১ চা-চামচ,\r\n১৪. টক দই ১ কাপ।', '> মাংস পাটায় ছেঁচে কিমা করুন। তেলে তেজপাতা, গরম মসলা ও পেয়াজ দিয়ে ভাজুন। পেঁয়াজ হারকা বাদামি রং হলে তুলে রাখুন। এক চা-চামচ জিরা তেলে অল্প ভেজে পেঁয়াজের সাথে তুলে রাখুন। ভাজা পেয়াজ ও জিরা বেটে রাখুন। এবার তেলে মাংস দিয়ে ভাজুন। মাংস ভাজা হলে অন্যান্য বাটা মসলা, দই, ও লবণ মাংসে দিন মাংস সেদ্ধ হওয়ার জন্য অল্প পানি দিন। ঢেকে মৃদু আঁচে রান্না করুন। পানি শুকালে মাংস কষান। নামাবার আগে বাটা পেঁয়াজ ও গরম মসলা দিয়ে অল্পক্ষন কষিয়ে তেল মাংসের উপরে উঠলে নামিয়ে গরম গরম পরিবেশন করুন ভুনা কাবাব।', NULL, 'পরিবর্তন.কম, ছবি : ইন্টারনেট', ' কাবাব_1536642909.jpg', '2018-09-11 09:15:10', '2018-09-11 09:15:10'),
(33, 'শিক কাবাব', NULL, NULL, NULL, '১. হাড্ডি ছাড়া গরুর মাংস কিউব করে কাটা ১ কেজি ,\r\n২. ধনিয়া গুঁড়ো ১ টেবিল চামচ ,\r\n৩. জিরা গুঁড়ো ২ টেবিল চামচ ,\r\n৪. গরম মসলা গুঁড়ো ১ টেবিল চামচ ,\r\n৫. মরিচ গুঁড়ো ২ টেবিল চামচ পু,\r\n৬. দিনা পাতা ৫০ গ্রাম ,\r\n৭. টক দই ৩ টেবিল চামচ ,\r\n৮. ধনিয়াপাতা ৫০ গ্রাম ,\r\n৯. কাঁচামরিচ ৫টি ,\r\n১০. আদা-রসুন বাটা দুই টেবিল চামচ ,\r\n১১. পেপে বাটা ১/৪ কাপ ,\r\n১২. সরিষার তেল ৪ টেবিল চামচ।', '> গরুর মাংসের টুকরাগুলোর সাথে সব উপকরণ একসঙ্গে মাখিয়ে নিন।\r\n> কমপক্ষে ২ ঘণ্টা মেরিনেট করে রাখুন।\r\n> তারপর মাংসের টুকরাগুলোকে শিকে গেঁথে নিন।\r\n> বারবিকিউ এর চুলায় কয়লা জ্বালিয়ে নিন।\r\n> কয়লার উপর শিকগুলো দিয়ে ঘুরিয়ে ঘুরিয়ে সব পাশ সেদ্ধ করে হালকা পোড়া পোড়া করে নিন।\r\n> সেদ্ধ হয়ে গেলে নান রুটি বা পরোটার সাথে সালাদ দিয়ে পরিবেশন করুন মজাদার শিক কাবাব।', NULL, 'পরিবর্তন.কম, ছবি : ইন্টারনেট', ' কাবাব_1536643048.jpg', '2018-09-11 09:17:29', '2018-09-11 09:17:29'),
(34, 'শামি কাবাব', NULL, NULL, NULL, '১. হাড় ছাড়া গরুর মাংস আধা কেজি,\r\n২. ডিম একটি,\r\n৩. পেঁয়াজ কুচি তিন টেবিল চামচ,\r\n৪. আদা বাটা এক চা চামচ,\r\n৫. রসুন বাটা এক চা চামচ,\r\n৬. ধনিয়া গুঁড়া এক চা চামচ,\r\n৭. বুটের ডাল এক কাপ,\r\n৮. জিরা গুঁড়া এক চা চামচ,\r\n৯. গরম মসলা গুঁড়া এক চা চামচ,\r\n১০. কাঁচামরিচ কুচি চার/পাঁচটি,\r\n১১. মরিচের গুঁড়া আদা চা চামচ,\r\n১২. হলুদের গুঁড়া আধা চা চামচ,\r\n১৩. তেল ভাজার জন্য,\r\n১৪. লবণ স্বাদমতো।', '> প্রথমে একটি প্যানে গরুর মাংস, বুটের ডাল, (অর্ধেকটা পেঁয়াজ কুচি, আদা-রসুন বাটা, হলুদ, মরিচ, দারুচিনি গুঁড়া, জিরা গুঁড়া, ধনিয়া গুঁড়া,) সামান্য লবণ ও দেড় কাপ পানি দিয়ে সেদ্ধ করুন। সেদ্ধ হয়ে গেলে শিলপাটায় বেটে নিন। এবার একটি বাটিতে এই মিশ্রণ নিয়ে বাকিটা পেঁয়াজ কুচি, বাকি অর্ধেক মসলা (ভাকি অর্ধেক গুঁড়ো মসলা) কাঁচামরিচ কুচি, ডিম ও লবণ দিয়ে ভালো করে মেখে গোল গোল টিকিয়ার মতো তৈরি করে নিন।\r\n> এবার প্যানে তেল দিয়ে তাতে টিকিয়াগুলো বাদামি করে ভেজে নিন। ব্যস, খুব সহজেই তৈরি হয়ে গেল বিফ শামী কাবাব।', NULL, 'পরিবর্তন.কম, ছবি : ইন্টারনেট', ' কাবাব_1536667077.jpg', '2018-09-11 15:57:59', '2018-09-11 15:57:59'),
(35, 'গরুর মাংসের গ্লাসি', NULL, NULL, NULL, '১. দেড় কেজি গরুর মাংস,\r\n২. পেঁয়াজবাটা ১/২ কাপ,\r\n৩. আদাবাটা ২ টেবিল-চামচ,\r\n৪. রসুনবাটা ১ চা-চামচ,\r\n৫. ধনিয়াবাটা ১ চা-চামচ,\r\n৬. পোস্তবাটা ১ টেবিল-চামচ,\r\n৭. দুধ ১ কাপ,\r\n৮. কাঁচা মরিচ ১০টি,\r\n৯. সাদা গোলমরিচের গুঁড়া ১ চা-চামচ,\r\n১০. টকদই ১ কাপ,\r\n১১. পরিমাণম তো লবণ,\r\n১২. ঘি ১ কাপ,\r\n১৩. মাওয়া ২ টেবিল-চামচ,\r\n১৪. এলাচ ৪টা,\r\n১৫. দারচিনি ৪ টুকরা,\r\n১৬. তেজপাতা ২টা,\r\n১৭. মিষ্টি দই ২ টেবিল-চামচ,\r\n১৮. পেঁয়াজকুচি ২ কাপ,\r\n১৯. জয়ত্রী-জায়ফলগুঁড়া আধা চা-চামচ,\r\n২০. গরম মসলা গুঁড়া ১ চা-চামচ,\r\n২১. গোলাপ পানি ১ চা-চামচ।', '> জয়ত্রী-জয়ফল, আদা, রসুনবাটা, ধনিয়াবাটা, পেঁয়াজবাটা, সাদা গোল মরিচ গুঁড়া, সিকি কাপ টকদই ও ঘি একটি পাত্রে ভালো মতো মাখিয়ে নিয়ে চুলায় এক ঘণ্টা রাখুন। এবার লবণ, দারচিনি, এলাচ, তেজপাতা দিন। অন্য একটি পাত্রে এক কাপ ঘি গরম করে পেঁয়াজ বাদামি করে ভেজে মসলা মাখানো মাংস দিয়ে ভালোভাবে কষিয়ে নিন। দেখে নিন, মাংস সেদ্ধ হলো কি না। এবার কাঁচা মরিচ ও গরম মসলা মাংসের ওপর ছড়িয়ে দিয়ে কিছুক্ষণ চুলায় রেখে নামিয়ে ফেলুন। হয়ে গেল গরুর মাংসের গ্লাসি। এবার গরম গরম পরিবেশন করুন।', NULL, 'পরিবর্তন.কম, ছবি : ইন্টারনেট', ' মাংসের গ্লাসি_1536667240.jpg', '2018-09-11 16:00:41', '2018-09-11 16:00:41'),
(36, 'গরুর টিকিয়া', NULL, NULL, NULL, '১. গরুর কিমা মাংস ৪ কাপ,\r\n২. তেল প্রয়োজনমতো,\r\n৩. খেসারির ডাল ২৫০ গ্রাম,\r\n৪. রসুন কাটা ২ টেবিল চামচ,\r\n৫. পেঁয়াজ কাটা ২ টেবিল চামচ,\r\n৬. দারুচিনি ৩-৪ টুকরা,\r\n৭. কাঁচা মরিচ ৮-১০টি (কুচি করা)।', '> সব একসঙ্গে চুলায় বসিয়ে দিন। সেদ্ধ হয়ে গেলে নামিয়ে ঠান্ডা করুন। সেদ্ধ করার সময় প্রয়োজন মনে করলে একটু তেল দিতে পারেন। এরপর ব্লেন্ড করে নিন। বেরেস্তা ভেজে নিন। কাঁচা মরিচ কুচিটা চাইলে বেরেস্তার সঙ্গে ভেজে নিতে পারেন। এবার ব্লেন্ড করা কিমার সঙ্গে বেরেস্তা মেশান। এবার গোল করে টিকিয়ার আকারে করে নিন। তেলের মধ্যে লাল করে ভেজে তুলুন।', NULL, 'শারমীন সুলতানা সুমি, প্রথম আলো', ' টিকিয়া_1536667394.jpg', '2018-09-11 16:03:15', '2018-09-11 16:03:15'),
(37, 'সাতকরা গোশত', NULL, NULL, NULL, '১. গরুর মাংস ৫০০ গ্রাম,\r\n২. টক দই আধা কাপ,\r\n৩. পেঁয়াজ বাটা আধা কাপ,\r\n৪. রসুন বাটা ১ টেবিল চামচ,\r\n৫. আদা বাটা আধা টেবিল চামচ,\r\n৬. জিরা গুঁড়া আধা চা-চামচ,\r\n৭. কালোজিরা ১ চা-চামচ,\r\n৮. মরিচ গুঁড়া ১ চা–চামচ,\r\n৯. সাতকরা (ছোট কিউব করে কাটা) ৪ টেবিল চামচ,\r\n১০. লবণ, তেল ও কাঁচা মরিচ পরিমাণমতো।\r\n\r\nফোড়নের জন্য :\r\n১. রসুন ২০ কোয়া,\r\n২. ভাজা শুকনো মরিচের গুঁড়া ১ চা-চামচ,\r\n৩. পাঁচফোড়ন গুঁড়া ১ চা-চামচ,\r\n৪. সরিষার তেল ৪ টেবিল চামচ।', '> তেল, সাতকরা ও কাঁচা মরিচ ছাড়া বাকি সব উপকরণ দিয়ে মাংস মেখে দুই ঘণ্টা রেখে দিতে হবে। এরপর চুলায় বসিয়ে মাঝারি আঁচে রান্না করতে হবে। মাংস সেদ্ধ না হওয়া পর্যন্ত যদি পানি শুকিয়ে যায় তাহলে পরিমাণমতো গরম পানি দিয়ে ঢেকে জ্বাল দিতে হবে। মাংস সেদ্ধ হয়ে এলে পাত্র নামিয়ে নিন। এবার অন্য একটি পাত্রে তেল গরম করে ফোড়নের মসলা ও সাতকরা দিয়ে কষাতে থাকুন। সাতকরা সেদ্ধ হয়ে এলে মাংসের মধ্যে এই মিশ্রণটুকু ঢেলে দিন। মাংসের পাত্রটি আবার চুলায় বসিয়ে নেড়ে দিয়ে দুই থেকে তিন মিনিট রেখে নামিয়ে নিন।', NULL, 'কাজী তাসলিমা সায়েরা, প্রথম আলো', ' গোশত_1536667559.jpg', '2018-09-11 16:05:59', '2018-09-11 16:05:59'),
(38, 'ছেঁচা মাংস', NULL, NULL, NULL, 'প্রথম ধাপের জন্য :\r\n১. গরুর রানের মাংস ৪ কেজি (হাড়-চর্বি ছাড়া),\r\n২. পেঁয়াজবাটা ৪ টেবিল চামচ,\r\n৩. আদাবাটা ৪ চা-চামচ,\r\n৪. রসুনবাটা ৪ চা-চামচ,\r\n৫. হলুদ গুঁড়া ৪ চা-চামচ,\r\n৬. মরিচ গুঁড়া ৪ চা-চামচ,\r\n৭. ধনিয়া গুঁড়া ৪ চা-চামচ,\r\n৮. তেজপাতা ৪টি,\r\n৯. লবঙ্গ, এলাচি, দারুচিনি ৪টি করে, \r\n১০. সয়াবিন তেল ১ কাপ,\r\n১১. লবণ পরিমাণমতো,\r\n১২. পানি পরিমাণমতো,\r\n১৩. গরুর চর্বি ১ কেজি।\r\n\r\nদ্বিতীয় ধাপের জন্য :\r\n১. পেঁয়াজ কুচি ১ কাপ,\r\n২. কাঁচা মরিচ কুচি ১ টেবিল চামচ,\r\n৩. ধনিয়াপাতা কুচি ২ টেবিল চামচ,\r\n৪. টালা জিরা গুঁড়া ২ টেবিল চামচ,\r\n৫. টালা ধনিয়া গুঁড়া ১ টেবিল চামচ,\r\n৬. গরমমসলা গুঁড়া ১ চা-চামচ,\r\n৭. টালা রাঁধুনী গুঁড়া আধা চা-চামচ,\r\n৮. টালা গোলমরিচ গুঁড়া ১ চা-চামচ,\r\n৯. সরিষার তেল ২ টেবিল চামচ,\r\n১০. লেবুর রস স্বাদমতো,\r\n১১. লবণ স্বাদমতো,\r\n১২. টমেটো কুচি আধা কাপ,\r\n১৩. কাটা শসা পরিমাণমতো।', '> গরুর রানের মাংস চর্বি এবং হাড় ছাড়া নিয়ে প্রতিটি টুকরা ৫০০ গ্রামের মতো নিতে হবে। ধুয়ে পানি ঝরাতে হবে। প্রথম ধাপের সব উপকরণ (চর্বি ছাড়া) মেখে রান্না করে নিতে হবে। এভাবে প্রতিদিন দুই বেলা করে তিন দিন জ্বাল দিতে হবে। পানি শুকিয়ে এলে মসলা থেকে মাংস তুলে নিন। অন্য ডেকচিতে চর্বি জ্বাল দিয়ে রাখতে হবে কোরবানির দিন থেকে। চর্বি গলে তেল বের হবে। আরেকটা ডেকচিতে এই তেল নিয়ে তুলে রাখা মাংস জ্বাল দিতে হবে। চর্বির তেলে যেন মাংস ডোবা থাকে। এভাবে গরমকাল হলে প্রতিদিন বা তিন দিন, শীতকাল হলে কয়েক দিন পরপর জ্বাল দিয়ে এই মাংস তিন-চার মাস সংরক্ষণ করা যায়। ছেঁচা মাংস করার সময় এই মাংসের চার টুকরা নিয়ে ছোট ছোট কুচি করে আবার পাটা বা হামানদিস্তায় ছেঁচে নিতে হবে। এবার দ্বিতীয় ধাপের সব উপকরণ (লেবুর রস ছাড়া) মেখে আবার গরম করে ডিশে ঢেলে ওপরে লেবুর রস, কাঁচা পেঁয়াজ কুচি, শসা, ধনিয়াপাতা, কাঁচা মরিচ দিয়ে পরিবেশন করতে হবে।', NULL, 'জোবাইদা আশরাফ, প্রথম আলো', ' মাংস_1536669184.jpg', '2018-09-11 16:33:05', '2018-09-11 16:33:05');

